;; **

;; @@
(defn distinct-by
  [f xs]
  (loop [result [] seen #{} xs xs]
    (if (empty? xs)
      result
      (let [x (first xs)
            v (f x)]
        (if (seen v)
          (recur result seen (rest xs))
          (recur (conj result x) (conj seen v) (rest xs)))))))
