(defn remove-first
  "Remove the first occurrence of c from xs; return nil if xs does not contain c"
  [xs c]
  (loop [xs (seq xs) ys []]
    (when-let [x (first xs)]
      (if (= x c)
        (concat ys (rest xs))
        (recur (rest xs) (conj ys x))))))
